mapper.select:
/* approval.getApprovalState */
SELECT  CASE WHEN A.TOTAL_CNT = B.COMP_CNT THEN 'COMPLETE'
             WHEN B.COMP_CNT = 0 THEN 'REQUEST'
             ELSE 'PROCESS'
       END AS APPR_STATE
FROM   (
        SELECT COUNT(1) AS TOTAL_CNT FROM   TBL_EXP_APPROVAL_USER WHERE  APPROVAL_UUID = #{approvalUuid} 
       ) A,
       (
        SELECT COUNT(1) AS COMP_CNT  FROM   TBL_EXP_APPROVAL_USER WHERE  APPROVAL_UUID = #{approvalUuid} AND  STATE = 'COMPLETE'
       ) B
WHERE  1 = 1

mapper.update:
/*  approval.updateApproval */
UPDATE TBL_EXP_APPROVAL
SET    UPDATE_USER = #{updateUser}
      ,UPDATE_DATE = (TO_DATE(#{updateDate} , 'yyyy/mm/dd hh24:mi:ss') )

         ,STATE = #{state}

WHERE  1 = 1

    AND    UUID = #{uuid}

/*  approval.updateApprovalUser */
UPDATE TBL_EXP_APPROVAL_USER
SET    UPDATE_USER = #{updateUser}
      ,UPDATE_DATE = (TO_DATE(#{updateDate} , 'yyyy/mm/dd hh24:mi:ss') )

         ,APPROVAL_TYPE = #{approvalType}


         ,STATE = #{state}


         ,APPROVAL_COMMENT = #{approvalComment}

WHERE  1 = 1

    AND    UUID = #{uuid}


    AND    APPROVAL_UUID = #{approvalUuid}


    AND    APPROVAL_USER_ID = #{approvalUserId}

mapper.select:
/* approval.getApprovalUserState */ SELECT UUID ,REQUEST_USER ,(SELECT USER_NM FROM TBL_EXP_USER WHERE USER_ID = REQUEST_USER) AS REQUEST_USER_NM ,TITLE ,CONTENTS ,STATE ,APPROVAL_DATE ,TO_CHAR( APPROVAL_DATE , 'YYYY-MM-DD HH24:MI:SS' ) APPROVAL_DATE1 ,UPDATE_USER ,UPDATE_DATE ,CREATE_USER ,CREATE_DATE ,TO_CHAR( CREATE_DATE , 'YYYY-MM-DD HH24:MI:SS' ) CREATE_DATE1 FROM TBL_EXP_APPROVAL WHERE 1 = 1 AND UUID IN ( SELECT APPROVAL_UUID FROM TBL_EXP_APPROVAL_USER WHERE 1 = 1
AND APPROVAL_USER_ID = #{approvalUserId} /* 'windrider' */
/* 미결조회 */ AND APPROVAL_TYPE = 'APPR' /* 결재 */ AND STATE IS NULL /* 'DECISION' */
/* 기결조회 */ AND APPROVAL_TYPE = 'APPR' /* 결재 */ AND STATE = 'COMPLETE' /* 'COMPLETE' */
/* 통보 조회 */ AND APPROVAL_TYPE = 'NOTICE' /* 결재 */
) ORDER BY APPROVAL_DATE DESC

/* approval.getApprovalUser */ SELECT B.UUID ,B.APPROVAL_UUID ,B.APPROVAL_USER_ID ,(SELECT USER_NM FROM TBL_EXP_USER WHERE USER_ID = B.APPROVAL_USER_ID) AS APPROVAL_USER_NM ,B.STATE ,B.APPROVAL_COMMENT ,B.UPDATE_USER ,B.UPDATE_DATE ,B.CREATE_USER ,B.CREATE_DATE ,TO_CHAR( B.CREATE_DATE , 'YYYY-MM-DD HH24:MI:SS' ) CREATE_DATE1 FROM TBL_EXP_APPROVAL A JOIN TBL_EXP_APPROVAL_USER B ON 1 = 1 AND A.UUID= B.APPROVAL_UUID WHERE 1 = 1
AND A.REQUEST_USER = #{requestUser}
AND A.UUID = #{uuid}
ORDER BY B.CREATE_DATE DESC

/* approval.getApproval */ SELECT A.APPROVAL_ID ,A.SP_CSTM_ID ,A.USER_ID ,(SELECT USER_NM FROM TBL_EXP_USER WHERE USER_ID = A.USER_ID AND SP_CSTM_ID = A.SP_CSTM_ID) AS USER_NM ,A.TITLE ,A.CONTENTS ,A.STATE ,A.APPROVAL_DATE ,TO_CHAR( A.APPROVAL_DATE , 'YYYY-MM-DD HH24:MI:SS' ) APPROVAL_DATE1 ,A.COMPLETE_DATE ,TO_CHAR( A.COMPLETE_DATE , 'YYYY-MM-DD HH24:MI:SS' ) COMPLETE_DATE1 ,A.UPDATE_USER ,A.UPDATE_DATE ,TO_CHAR( A.UPDATE_DATE , 'YYYY-MM-DD HH24:MI:SS' ) UPDATE_DATE1 ,A.CREATE_USER ,TO_CHAR( A.CREATE_DATE , 'YYYY-MM-DD HH24:MI:SS' ) CREATE_DATE1 FROM TBL_EXP_APPROVAL A WHERE 1 = 1 /* 세션작업완료후 주석 풀기 AND A.SP_CSTM_ID = sessionVo.spCstmCd */
AND A.USER_ID = #{searchUserId}
AND A.APPROVAL_ID = #{approvalId}
AND STATE = #{state}
ORDER BY APPROVAL_DATE DESC

