mapper.select:
/* common.getCommonCodeList 공통코드 리스트 조회 */ SELECT G.GRP_CODE_NM , G.CODE_DESC AS GRP_CODE_DESC , C.CODE_NM , C.CODE_DESC FROM TBL_EXP_GRPCODE G , TBL_EXP_CODE C WHERE G.GRP_CODE_ID = C.GRP_CODE_ID
AND G.GRP_CODE_NM = #{GRP_CODE_NM}
AND C.CODE_NM = #{CODE_NM}
AND G.USE_YN = 'Y' AND G.DEL_YN = 'N' AND C.USE_YN = 'Y' AND C.DEL_YN = 'N' ORDER BY C.SORT_ORDER
