mapper.sql:
SELECT ROW_NUMBER() OVER (ORDER BY A.STORE_ID,A.YEAR,A.MONTH DESC) AS RN ,A.SP_CSTM_ID ,A.STORE_ID ,A.YEAR ,A.MONTH ,C.STORE_NM3 ,A.SUBJECT ,GET_CODE_NM('FINANCIAL_SUBJECT', A.SUBJECT) AS SUBJECT_NM ,A.AMOUNT AS AMOUNT FROM TBL_EXP_STORE_FINANCIAL A JOIN TBL_EXP_STORE_INFO C ON 1 = 1 AND A.SP_CSTM_ID = C.SP_CSTM_ID AND A.STORE_ID = C.STORE_ID WHERE 1 = 1 AND A.SP_CSTM_ID = #{parm.spCstmId}
AND A.STORE_ID = #{parm.storeId}
AND A.SUBJECT = #{parm.subject} AND A.YEAR = #{parm.year} AND A.MONTH = #{parm.month}
