mapper.select:
/* operation.getConsultingItem 컨설팅 조회 */ SELECT CT.CONSULTING_ID , CT.CONSULTING_NM , CT.CONSULTING_TYPE , CT.CONSULTING_DESC FROM TBL_EXP_CONSULTING CT , TBL_EXP_CONTRACT R WHERE 1=1
AND CT.CONSULTING_ID = #{CONSULTING_ID}
AND CT.CONSULTING_ID = R.CONSULTING_ID(+) ORDER BY CT.CONSULTING_NM

mapper.delete:
/*	operation.deleteConsultingItem
	컨설팅 삭제
*/
DELETE FROM  TBL_EXP_CONSULTING
WHERE CONSULTING_ID	= #{CONSULTING_ID}
