mapper.insert:
/* Sample.setFile */ INSERT INTO TBL_EXP_FILEMNG( UUID ,GRP_ID ,ORG_FILE_NAME ,STORED_FILE_NAME ,FILE_PATH
,FILE_SIZE ,FILE_TYPE ,CREATE_USER ,CREATE_DATE ) VALUES( #{uuid}, #{grpId}, #{orgFileName}, #{storedFileName}, #{filePath},
TO_NUMBER(#{fileSize}), #{fileType}, #{createUser}, (TO_DATE(#{createDate} , 'yyyy/mm/dd hh24:mi:ss') ) )

mapper.select:
/* Sample.getFile */ SELECT UUID AS uuid ,GRP_ID AS grpId ,ORG_FILE_NAME AS orgFileName ,STORED_FILE_NAME AS storedFileName ,FILE_PATH AS filePath ,FILE_SIZE AS fileSize ,FILE_TYPE AS fileType ,UPDATE_USER AS updateUser ,UPDATE_DATE AS updateDate ,CREATE_USER AS createUser ,CREATE_DATE AS createDate FROM TBL_EXP_FILEMNG WHERE 1 = 1
AND GRP_ID = #{grpId}
AND UUID = #{uuid}
ORDER BY CREATE_DATE DESC

mapper.delete:
/* Sample.deleteFile */
DELETE FROM TBL_EXP_FILEMNG
WHERE  1 = 1

  AND    GRP_ID = #{grpId}


  AND    UUID = #{uuid}
