mapper.insert:
/* schedule.insertScheduleMsater */
INSERT INTO TBL_EXP_SCHEDULE_MASTER (
      SCHEDULE_GRP_ID     
     ,LOOP_TYPE           
     ,SCHEDULE_START_DATE 
     ,SCHEDULE_END_DATE
)
VALUES(
      #{scheduleGrpId}
     ,#{loopType}
     ,#{scheduleStartDate}
     ,#{scheduleEndDate}
)

mapper.update:
/*  schedule.updateScheduleMaster */
UPDATE TBL_EXP_SCHEDULE_MASTER
SET    LOOP_TYPE           = #{loopType}
      ,SCHEDULE_START_DATE = #{scheduleStartDate} 
      ,SCHEDULE_END_DATE   = #{scheduleEndDate}
WHERE 1 = 1
AND    SCHEDULE_GRP_ID = #{scheduleGrpId}

mapper.delete:
/*  schedule.deleteScheduleMsater */
DELETE FROM TBL_EXP_SCHEDULE_MASTER 
WHERE SCHEDULE_GRP_ID = #{scheduleGrpId}

mapper.update:
/*  schedule.updateSchedule */
UPDATE TBL_EXP_SCHEDULE
SET    TITLE              = #{title}
      ,WORK_TYPE          = #{workType} 
      ,ALL_APPLY_YN       = #{allApplyYn}
      ,TIME_TYPE          = 'TIME_TYPE'
      ,START_TIME_HOUR    = #{startTimeH}
      ,START_TIME_MINUTE  = #{startTimeM}
      ,END_TIME_HOUR      = #{endTimeH}
      ,END_TIME_MINUTE    = #{endTimeM}
      ,POSITION           = #{position}
      ,SECRET_YN          = #{secretYn}
      ,DESCRIPTION        = #{description}
WHERE 1 = 1
AND    SCHEDULE_ID = #{scheduleId}
AND    USER_ID     = #{sessionVo.userId}
AND    SP_CSTM_ID  = #{sessionVo.spCstmId}

mapper.delete:
/*  schedule.deleteSchedule */
DELETE FROM TBL_EXP_SCHEDULE 
WHERE 1 = 1

    AND    SCHEDULE_GRP_ID =  #{scheduleGrpId}


    AND   SCHEDULE_ID = #{scheduleId}

/*  schedule.deleteScheduleShare */
DELETE FROM TBL_EXP_SCHEDULE_SHARE 
WHERE  1 = 1

    AND    SCHEDULE_ID = #{scheduleId}


    AND    USER_ID     = #{userId}


    AND    SP_CSTM_ID  = #{spCstmId}

mapper.insert:
/* schedule.insertSchedule */
INSERT INTO TBL_EXP_SCHEDULE (
      SCHEDULE_ID 
     ,SCHEDULE_GRP_ID     
     ,TITLE             
     ,WORK_TYPE  
     ,ALL_APPLY_YN       
     ,SCHEDULE_DATE     
     ,TIME_TYPE         
     ,START_TIME_HOUR   
     ,START_TIME_MINUTE 
     ,END_TIME_HOUR     
     ,END_TIME_MINUTE   
     ,POSITION          
     ,SECRET_YN         
     ,DESCRIPTION       
     ,USER_ID     
     ,SP_CSTM_ID      
)
VALUES(
      #{scheduleId}
     ,#{scheduleGrpId}
     ,#{title}
     ,#{workType} 
     ,#{allApplyYn}          
     ,(TO_DATE(#{date}, 'yyyy-mm-dd') )
     ,'TIME_TYPE'
     ,#{startTimeH}
     ,#{startTimeM}
     ,#{endTimeH}
     ,#{endTimeM}
     ,#{position}
     ,#{secretYn}
     ,#{description}
     ,#{sessionVo.userId}
     ,#{sessionVo.spCstmId}
)

/* schedule.insertScheduleShare */
INSERT INTO TBL_EXP_SCHEDULE_SHARE (
     SCHEDULE_ID     
    ,USER_ID 
    ,SP_CSTM_ID    
   )
VALUES(
     #{scheduleId}
    ,#{userId}
    ,#{spCstmId}
   )

mapper.select:
/* schedule.getScheduleList */ WITH SCHEDULE AS ( SELECT A.SCHEDULE_ID ,A.TITLE ,A.WORK_TYPE ,A.ALL_APPLY_YN ,B.ATTR1 AS WORK_COLOR ,B.ATTR2 AS TEXT_COLOR ,A.SCHEDULE_DATE ,TO_CHAR(A.SCHEDULE_DATE,'YYYY-MM-DD') AS SCHEDULE_DATE1 ,A.TIME_TYPE ,A.START_TIME_HOUR ,A.START_TIME_MINUTE ,A.END_TIME_HOUR ,A.END_TIME_MINUTE ,A.POSITION ,A.SECRET_YN ,A.DESCRIPTION ,A.USER_ID ,A.SP_CSTM_ID ,C.LOOP_TYPE ,C.SCHEDULE_GRP_ID ,TO_CHAR(C.SCHEDULE_START_DATE,'YYYY-MM-DD') AS SCHEDULE_START_DATE ,TO_CHAR(C.SCHEDULE_END_DATE,'YYYY-MM-DD') AS SCHEDULE_END_DATE FROM TBL_EXP_SCHEDULE A JOIN ( SELECT A.GRP_CODE_ID ,B.CODE_ID ,B.ATTR1 ,B.ATTR2 FROM TBL_EXP_GRPCODE A JOIN TBL_EXP_CODE B ON 1 = 1 AND B.DEL_YN = 'N' AND A.GRP_CODE_ID = B.GRP_CODE_ID WHERE 1 = 1 AND A.DEL_YN = 'N' AND A.GRP_CODE_ID = 'SCHEDULE_WORK_TYPE' ) B ON 1 = 1 AND A.WORK_TYPE = B.CODE_ID JOIN TBL_EXP_SCHEDULE_MASTER C ON 1 = 1 AND A.SCHEDULE_GRP_ID = C.SCHEDULE_GRP_ID WHERE 1 = 1
AND A.SCHEDULE_ID = #{scheduleId}
AND TRIM(A.TITLE) LIKE '%' || TRIM(#{title})||'%'
) SELECT SCHEDULE.SCHEDULE_ID ,SCHEDULE.SP_CSTM_ID ,SCHEDULE.USER_ID ,SCHEDULE.TITLE ,'N' AS SHARE_YN ,SCHEDULE.WORK_TYPE ,SCHEDULE.ALL_APPLY_YN ,SCHEDULE.WORK_COLOR ,SCHEDULE.TEXT_COLOR ,SCHEDULE.SCHEDULE_DATE ,SCHEDULE.SCHEDULE_DATE1 ,SCHEDULE.TIME_TYPE ,SCHEDULE.START_TIME_HOUR ,SCHEDULE.START_TIME_MINUTE ,SCHEDULE.END_TIME_HOUR ,SCHEDULE.END_TIME_MINUTE ,SCHEDULE.POSITION ,SCHEDULE.SECRET_YN ,SCHEDULE.DESCRIPTION ,SCHEDULE.USER_ID ,SCHEDULE.LOOP_TYPE ,SCHEDULE.SCHEDULE_GRP_ID ,SCHEDULE.SCHEDULE_START_DATE ,SCHEDULE.SCHEDULE_END_DATE FROM SCHEDULE WHERE 1 = 1 AND SCHEDULE.USER_ID = #{sessionVo.userId} AND SCHEDULE.SP_CSTM_ID = #{sessionVo.spCstmId} UNION SELECT SCHEDULE.SCHEDULE_ID ,SCHEDULE.SP_CSTM_ID ,SCHEDULE.USER_ID ,'['||GET_USER_NM(SCHEDULE.USER_ID)||']'||SCHEDULE.TITLE AS TITLE ,'Y' AS SHARE_YN ,SCHEDULE.WORK_TYPE ,SCHEDULE.ALL_APPLY_YN ,SCHEDULE.WORK_COLOR ,SCHEDULE.TEXT_COLOR ,SCHEDULE.SCHEDULE_DATE ,SCHEDULE.SCHEDULE_DATE1 ,SCHEDULE.TIME_TYPE ,SCHEDULE.START_TIME_HOUR ,SCHEDULE.START_TIME_MINUTE ,SCHEDULE.END_TIME_HOUR ,SCHEDULE.END_TIME_MINUTE ,SCHEDULE.POSITION ,SCHEDULE.SECRET_YN ,SCHEDULE.DESCRIPTION ,SCHEDULE.USER_ID ,SCHEDULE.LOOP_TYPE ,SCHEDULE.SCHEDULE_GRP_ID ,SCHEDULE.SCHEDULE_START_DATE ,SCHEDULE.SCHEDULE_END_DATE FROM TBL_EXP_SCHEDULE_SHARE A JOIN SCHEDULE ON 1 = 1 AND SCHEDULE.SECRET_YN = 'Y' AND A.SCHEDULE_ID = SCHEDULE.SCHEDULE_ID AND SCHEDULE.SP_CSTM_ID = #{sessionVo.spCstmId} AND SCHEDULE.USER_ID != #{sessionVo.userId} WHERE 1 = 1 AND A.SP_CSTM_ID = #{sessionVo.spCstmId} AND A.USER_ID = #{sessionVo.userId}

/* schedule.getScheduleList1 */ SELECT A.SCHEDULE_ID ,A.SCHEDULE_GRP_ID ,A.TITLE ,A.WORK_TYPE ,GET_CODE_NM('SCHEDULE_WORK_TYPE', A.WORK_TYPE) AS WORK_TYPE_NM ,A.ALL_APPLY_YN ,A.SCHEDULE_DATE ,TO_CHAR(A.SCHEDULE_DATE ,'YYYY-MM-DD') AS SCHEDULE_DATE_YMD ,A.TIME_TYPE ,A.START_TIME_HOUR ,GET_CODE_NM('TIME_HOUR_24', A.START_TIME_HOUR) AS START_TIME_HOUR_NM ,A.START_TIME_MINUTE ,GET_CODE_NM('TIME_MINUTE_30', A.START_TIME_MINUTE) AS START_TIME_MINUTE_NM ,A.END_TIME_HOUR ,GET_CODE_NM('TIME_HOUR_24', A.END_TIME_HOUR) AS END_TIME_HOUR_NM ,A.END_TIME_MINUTE ,GET_CODE_NM('TIME_MINUTE_30', A.END_TIME_MINUTE) AS END_TIME_MINUTE_NM ,A.POSITION ,A.SECRET_YN ,GET_CODE_NM('SCHEDULE_SECRET_YN', A.SECRET_YN) AS SECRET_YN_NM ,A.DESCRIPTION ,A.USER_ID ,GET_USER_NM(A.USER_ID) AS USER_NM ,A.SP_CSTM_ID FROM TBL_EXP_SCHEDULE A WHERE 1 = 1
AND A.SCHEDULE_GRP_ID = #{scheduleGrpId}
AND A.SCHEDULE_ID = #{scheduleId}
