      #{scheduleId}
     ,#{title}
     ,#{workType}         
     ,(TO_DATE(#{date}, 'yyyy-mm-dd') )
     ,#{loopType}
     ,'TIME_TYPE'
     ,#{startTimeH}
     ,#{startTimeM}
     ,#{endTimeH}
     ,#{endTimeM}
