mapper.update:
/*  schedule.updateSchedule */
UPDATE TBL_EXP_SCHEDULE
SET    TITLE              = #{title}
      ,WORK_TYPE          = #{workType} 
      ,TIME_TYPE          = 'TIME_TYPE'
      ,START_TIME_HOUR    = #{startTimeH}
      ,START_TIME_MINUTE  = #{startTimeM}
      ,END_TIME_HOUR      = #{endTimeH}
      ,END_TIME_MINUTE    = #{endTimeM}
      ,POSITION           = #{position}
      ,SECRET_YN          = #{secretYn}
      ,DESCRIPTION        = #{description}
WHERE 1 = 1
AND   SCHEDULE_ID = #{scheduleId}

mapper.delete:
/*  schedule.deleteSchedule */
DELETE FROM TBL_EXP_SCHEDULE 
WHERE SCHEDULE_ID = #{scheduleId}

/*  schedule.deleteScheduleShare */
DELETE FROM TBL_EXP_SCHEDULE_SHARE 
WHERE  1 = 1
AND    SCHEDULE_ID = #{scheduleId}

    AND    USER_ID = #{userId}

mapper.insert:
/* schedule.insertSchedule */
INSERT INTO TBL_EXP_SCHEDULE (
      SCHEDULE_ID       
     ,TITLE             
     ,WORK_TYPE         
     ,SCHEDULE_DATE     
     ,LOOP_TYPE         
     ,TIME_TYPE         
     ,START_TIME_HOUR   
     ,START_TIME_MINUTE 
     ,END_TIME_HOUR     
     ,END_TIME_MINUTE   
     ,POSITION          
     ,SECRET_YN         
     ,DESCRIPTION       
     ,USER_ID           
)
VALUES(
      #{scheduleId}
     ,#{title}
     ,#{workType}         
     ,(TO_DATE(#{date}, 'yyyy-mm-dd') )
     ,#{loopType}
     ,'TIME_TYPE'
     ,#{startTimeH}
     ,#{startTimeM}
     ,#{endTimeH}
     ,#{endTimeM}
     ,#{position}
     ,#{secretYn}
     ,#{description}
     ,#{userId}
)

/* schedule.insertScheduleShare */
INSERT INTO TBL_EXP_SCHEDULE_SHARE (
     SCHEDULE_ID     
    ,USER_ID     
   )
VALUES(
     #{scheduleId}
    ,#{userId}
   )
